import './Login.css';
import React,{useState} from 'react'
import {GoogleLogin,GoogleLogout} from 'react-google-login';
import { GoogleOAuthProvider } from '@react-oauth/google';

const Login = () => {
    <GoogleOAuthProvider clientId="1061457779156-ji41itt43ki638rjeh6v9slkorm4idop.apps.googleusercontent.com">...</GoogleOAuthProvider>;

    const clientId="1061457779156-ji41itt43ki638rjeh6v9slkorm4idop.apps.googleusercontent.com";

    const [showLoginButton,setShowLoginButton]= useState(true);
    const [showLogoutButton,setShowLogoutButton]=useState(false);

    const OnLoginSuccess=(res)=>{
        console.log("Login success",res.profileObj);
        setShowLoginButton(false);
        setShowLogoutButton(true);
    }

    const OnFailureSuccess=(res)=>{
        console.log("Login Failed",res);
    }

    const OnLogoutSuccess=()=>{
        alert("You have been signed out successfully");
        setShowLoginButton(true);
        setShowLogoutButton(false);

    }

  return (
    <div>
        {showLoginButton ?
            <GoogleLogin
            clientId={clientId}
            buttonText="Login"
            onSuccess={OnLoginSuccess}
            onFailure={OnFailureSuccess}
            cookiePolicy={'single_host_origin'}
            />  :null
        }
        {showLogoutButton ? 

            <GoogleLogout
            clientId={clientId}
            buttonText="Logout"
            onLogoutSuccess={OnLogoutSuccess}
            >
            </GoogleLogout> :null
        }      
    </div>
  )
}

export default Login
